mapper.select:
select
  id, staff_code, username, password, email_address, vote_area, cardinal_number, elect_number, vote_number, is_manager, is_vote, percentage, status
from Test.user_info


        and id = #{id}


        and staff_code = #{staffCode}


        and username = #{username}


        and password = #{password}


        and email_address = #{emailAddress}


        and vote_area = #{voteArea}


        and cardinal_number = #{cardinalNumber}


        and elect_number = #{electNumber}


        and vote_number = #{voteNumber}


        and is_manager = #{isManager}


        and is_vote = #{isVote}


        and percentage = #{percentage}


        and status = #{status}
